import React, { useContext, useState } from "react";
import axios from 'axios';
import { addIncome, getIncomes, deleteIncome } from './income';  // Importamos funciones desde 'income'
import { addExpense, getExpenses, deleteExpense } from './expense';  // Importamos funciones desde 'expense'

const BASE_URL = "http://localhost:4000/api/v1/";

const GlobalContext = React.createContext();

export const GlobalProvider = ({ children }) => {

    const [incomes, setIncomes] = useState([]);
    const [expenses, setExpenses] = useState([]);
    const [error, setError] = useState(null);
    const [financialAdvice, setFinancialAdvice] = useState(null);
    const [loadingAdvice, setLoadingAdvice] = useState(false);

    // Función para obtener el asesoramiento financiero
    const getFinancialAdvice = async () => {
        setLoadingAdvice(true);  // Indicamos que estamos cargando los datos

        try {
            const response = await axios.get(`${BASE_URL}get-financial-advice`);
            setFinancialAdvice(response.data);  // Guardamos el asesoramiento en el estado
        } catch (err) {
            setError(err.response ? err.response.data.message : 'Error fetching financial advice');
        } finally {
            setLoadingAdvice(false);  // Indicamos que terminó la carga
        }
    };

    // Cálculo de ingresos
    const totalIncome = () => {
        let totalIncome = 0;
        incomes.forEach((income) => {
            totalIncome += income.amount;
        });
        return totalIncome;
    };

    // Cálculo de gastos
    const totalExpenses = () => {
        let totalExpenses = 0;
        expenses.forEach((expense) => {
            totalExpenses += expense.amount;
        });
        return totalExpenses;
    };

    // Balance total
    const totalBalance = () => {
        return totalIncome() - totalExpenses();
    };

    // Histórico de transacciones (ingresos + gastos)
    const transactionHistory = () => {
        const history = [...incomes, ...expenses];
        history.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        return history.slice(0, 3); // Mostramos las últimas 3 transacciones
    };

    return (
        <GlobalContext.Provider value={{
            addIncome,
            getIncomes,
            incomes,
            deleteIncome,
            expenses,
            totalIncome,
            addExpense,
            getExpenses,
            deleteExpense,
            totalExpenses,
            totalBalance,
            transactionHistory,
            financialAdvice,
            loadingAdvice,
            getFinancialAdvice,
            error,
            setError
        }}>
            {children}
        </GlobalContext.Provider>
    );
};

export const useGlobalContext = () => {
    return useContext(GlobalContext);
};
