import React, { useContext, useState } from "react";
import axios from 'axios';

const BASE_URL = "http://localhost:4000/api/v1/";

const GlobalContext = React.createContext();

export const GlobalProvider = ({ children }) => {

    const [incomes, setIncomes] = useState([]);
    const [expenses, setExpenses] = useState([]);
    const [error, setError] = useState(null);
    const [financialAdvice, setFinancialAdvice] = useState(null);  // Estado para el asesoramiento financiero
    const [loadingAdvice, setLoadingAdvice] = useState(false);  // Estado de carga para el asesoramiento

    // Función para obtener el asesoramiento financiero
    const getFinancialAdvice = async () => {
        setLoadingAdvice(true);  // Indicamos que estamos cargando los datos

        try {
            const response = await axios.get(`${BASE_URL}get-financial-advice`);
            setFinancialAdvice(response.data);  // Guardamos el asesoramiento en el estado
        } catch (err) {
            setError(err.response ? err.response.data.message : 'Error fetching financial advice');
        } finally {
            setLoadingAdvice(false);  // Indicamos que terminó la carga
        }
    };

    // ... Aquí siguen las demás funciones que ya tienes para ingresos y gastos

    return (
        <GlobalContext.Provider value={{
            addIncome,
            getIncomes,
            incomes,
            deleteIncome,
            expenses,
            totalIncome,
            addExpense,
            getExpenses,
            deleteExpense,
            totalExpenses,
            totalBalance,
            transactionHistory,
            financialAdvice,  // Exponemos el asesoramiento financiero
            loadingAdvice,  // Exponemos el estado de carga
            getFinancialAdvice,  // Exponemos la función para obtener el asesoramiento
            error,
            setError
        }}>
            {children}
        </GlobalContext.Provider>
    );
};

export const useGlobalContext = () => {
    return useContext(GlobalContext);
};
