import React, { useContext, useState, useCallback } from "react";
import axios from 'axios';

const BASE_URL = "http://localhost:4000/api/v1/";

const GlobalContext = React.createContext();

export const GlobalProvider = ({ children }) => {
    const [incomes, setIncomes] = useState([]);
    const [expenses, setExpenses] = useState([]);
    const [error, setError] = useState(null);
    
    // Estado para la asesoría financiera
    const [financialAdvice, setFinancialAdvice] = useState(null);
    const [loadingAdvice, setLoadingAdvice] = useState(false);

    // Función para obtener los consejos financieros
    const getFinancialAdvice = useCallback(async () => {
        setLoadingAdvice(true);
        try {
            const response = await axios.get(`${BASE_URL}financial-advice`); // Asume que existe un endpoint /financial-advice
            setFinancialAdvice(response.data); // Guarda los datos en el estado
        } catch (err) {
            setError(err.response?.data?.message || 'Error al obtener asesoría financiera');
        } finally {
            setLoadingAdvice(false);
        }
    }, []);

    // Lógica existente para manejar ingresos y gastos
    const addIncome = async (income) => {
        const response = await axios.post(`${BASE_URL}add-income`, income).catch((err) => {
            setError(err.response.data.message);
        });
        getIncomes();
    };

    const getIncomes = async () => {
        const response = await axios.get(`${BASE_URL}get-incomes`);
        setIncomes(response.data);
        console.log(response.data);
    };

    const deleteIncome = async (id) => {
        const res = await axios.delete(`${BASE_URL}delete-income/${id}`);
        getIncomes();
    };

    const totalIncome = () => {
        return incomes.reduce((total, income) => total + income.amount, 0);
    };

    const addExpense = async (expense) => {
        const response = await axios.post(`${BASE_URL}add-expense`, expense).catch((err) => {
            setError(err.response.data.message);
        });
        getExpenses();
    };

    const getExpenses = async () => {
        const response = await axios.get(`${BASE_URL}get-expenses`);
        setExpenses(response.data);
        console.log(response.data);
    };

    const deleteExpense = async (id) => {
        const res = await axios.delete(`${BASE_URL}delete-expense/${id}`);
        getExpenses();
    };

    const totalExpenses = () => {
        return expenses.reduce((total, expense) => total + expense.amount, 0);
    };

    const totalBalance = () => {
        return totalIncome() - totalExpenses();
    };

    const transactionHistory = () => {
        const history = [...incomes, ...expenses];
        history.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        return history.slice(0, 3);
    };

    return (
        <GlobalContext.Provider value={{
            addIncome,
            getIncomes,
            incomes,
            deleteIncome,
            expenses,
            totalIncome,
            addExpense,
            getExpenses,
            deleteExpense,
            totalExpenses,
            totalBalance,
            transactionHistory,
            error,
            setError,
            financialAdvice, // Agregar estado de asesoría financiera
            loadingAdvice,   // Estado de carga
            getFinancialAdvice, // Función para obtener asesoría financiera
        }}>
            {children}
        </GlobalContext.Provider>
    );
};

export const useGlobalContext = () => useContext(GlobalContext);
